prompt --application/shared_components/automations/dwh_ad_hoc_extracts
begin
wwv_flow_imp_shared.create_automation(
 p_id=>wwv_flow_imp.id(31060013938214783)
,p_name=>'DWH Ad-hoc Extracts'
,p_static_id=>'dwh-ad-hoc-extracts'
,p_trigger_type=>'API'
,p_polling_status=>'DISABLED'
,p_result_type=>'ROWS'
,p_location=>'LOCAL'
,p_use_local_sync_table=>false
,p_query_type=>'SQL'
,p_query_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select *',
'from dbo_msg_outbound',
'where msg_type = ''DWH''',
'and msg_status = ''RECEIVED''',
'and msg_source_ref like ''MANUAL%''',
'order by creation_date desc',
'fetch first 1 rows only'))
,p_include_rowid_column=>false
,p_pk_column_name=>'MSG_ID'
,p_max_rows_to_process=>1
,p_commit_each_row=>true
,p_error_handling_type=>'ABORT'
);
wwv_flow_imp_shared.create_automation_action(
 p_id=>wwv_flow_imp.id(31060385813214800)
,p_automation_id=>wwv_flow_imp.id(31060013938214783)
,p_name=>'Run Extract'
,p_execution_sequence=>10
,p_action_type=>'NATIVE_PLSQL'
,p_action_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_extract_id number;',
'    l_result     varchar2(30);',
'    l_err_msg    varchar(32767);',
'    l_count      number;',
'    t_parameters dwh_extract_parameter_tab := dwh_extract_parameter_tab();',
'    l_step       varchar2(100);',
'begin',
'',
'    l_step := ''Check if more than expected rows'';',
'    IF :SQL > 1 THEN',
'        ',
'        dbo_msg_pkg.update_outbound_status(',
'                p_msg_id => :MSG_ID,',
'                p_msg_status => ''ERROR'');',
'',
'        dbo_msg_pkg.log_error(:MSG_ID, ''More than one record found for processing. MSG_ID=''||:MSG_ID, ''ERROR'');',
'',
'        apex_automation.exit( p_log_message => ''More than one record found for processing. MSG_ID=''||:MSG_ID );',
'         ',
'    ELSE',
'        l_step := ''Update status to processing'';',
'        dbo_msg_pkg.update_outbound_status(',
'            p_msg_id => :MSG_ID,',
'            p_msg_status => ''PROCESSING'');',
'',
'        l_count :=0;',
'        l_step := ''Parse parameters json'';',
'        FOR rec IN (SELECT jt.extract_id, jt.position, jt.name, jt.value',
'                    FROM dual,',
'                     JSON_TABLE(',
'                         :MSG_PAYLOAD,',
'                         ''$''',
'                         COLUMNS (extract_id NUMBER PATH ''$.extract_id'',',
'                             NESTED PATH ''$.parameters[*]''',
'                             COLUMNS (position NUMBER PATH ''$.position'',',
'                                      name VARCHAR2(50) PATH ''$.name'',',
'                                      value VARCHAR2(50) PATH ''$.value'')',
'                         )) jt',
'                    order by jt.position)',
'        LOOP',
'            l_count := l_count +1;',
'            t_parameters.EXTEND;',
'            t_parameters(l_count) := dwh_extract_parameter_typ(rec.position, rec.name, rec.value);',
'            l_extract_id := rec.extract_id;',
'        END LOOP;',
'',
'        l_step := ''Run extract'';',
'        dwh_extract_pkg.run_extract(',
'            p_msg_id        => :MSG_ID,',
'            p_extract_id    => l_extract_id,',
'            p_parameters    => t_parameters,',
'            p_result        => l_result, ',
'            p_error_message => l_err_msg);',
'',
'        if l_result = ''Success'' then ',
'',
'            dbo_msg_pkg.update_outbound_status(',
'                p_msg_id => :MSG_ID,',
'                p_msg_status => ''PROCESSED'');',
'        else',
'            dbo_msg_pkg.update_outbound_status(',
'                p_msg_id => :MSG_ID,',
'                p_msg_status => ''ERROR'');',
'',
'            APEX_AUTOMATION.LOG_ERROR (p_message =>  l_err_msg);',
'            ',
'        end if;',
'    END IF;',
'',
'exception',
'    when others then',
'         dbo_msg_pkg.update_outbound_status(',
'                p_msg_id => :MSG_ID,',
'                p_msg_status => ''ERROR'');',
'',
'         dbo_msg_pkg.log_error(:MSG_ID, substr(l_step||''>> Unexpected Error: ''|| SQLERRM,1,4000), ''ERROR'');',
'',
'         APEX_AUTOMATION.LOG_ERROR (p_message => l_step||''>> Unexpected Error: ''|| SQLERRM);',
'',
'        ',
'end;'))
,p_action_clob_language=>'PLSQL'
,p_location=>'LOCAL'
,p_stop_execution_on_error=>true
);
end;
