,p_plug_template=>wwv_flow_imp.id(11722478133171065)
,p_plug_display_sequence=>30
,p_location=>null
,p_function_body_language=>'PLSQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  l_html    CLOB;',
'  l_value   VARCHAR2(200);',
'  l_bi_params DWH_EXTRACT_SETUP.bi_parameters%type;',
'BEGIN',
'',
'    select bi_parameters',
'    into l_bi_params',
'    from DWH_EXTRACT_SETUP',
'    where id = :P2_EXTRACT_ID;',
'',
'    if l_bi_params is not null then ',
'',
'        for rec in (SELECT jt.position, upper(jt.name) name, jt.value, jt.lov, jt.calculate_flag, jt.calc_attr1, jt.calc_attr2, jt.calc_attr3, des.name extract_name',
'                    FROM DWH_EXTRACT_SETUP des,',
'                     JSON_TABLE(des.bi_parameters, ''$.parameters[*]''',
'                                COLUMNS (',
'                                  position   VARCHAR2(100) PATH ''$.position'',',
'                                  name       VARCHAR2(100) PATH ''$.name'',',
'                                  value       VARCHAR2(100) PATH ''$.value'',',
'                                  lov         VARCHAR2(100) PATH ''$.lov'',',
'                                  calculate_flag    VARCHAR2(1) PATH ''$.calculate_flag'',',
'                                  calc_attr1    VARCHAR2(1) PATH ''$.calc_attr1'',',
'                                  calc_attr2    VARCHAR2(1) PATH ''$.calc_attr2'',',
'                                  calc_attr3    VARCHAR2(1) PATH ''$.calc_attr3''',
'                                )) jt',
'                    where des.id = :P2_EXTRACT_ID',
'                    order by jt.position)',
'        loop',
'            l_value := NULL;',
'',
'            if rec.calculate_flag = ''Y'' then',
'                l_value := dwh_extract_pkg.calculate_parameter_value(',
'                                p_extract_name => rec.extract_name, ',
'                                p_parameter_name => rec.name, ',
'                                p_calc_attr1 => rec.calc_attr1,',
'                                p_calc_attr2 => rec.calc_attr2,',
'                                p_calc_attr3 => rec.calc_attr3);',
'            else',
'                l_value := rec.value;',
'            end if;',
'            ',
'            if rec.lov is not null then',
'                l_html := l_html || rec.name || APEX_ITEM.SELECT_LIST_FROM_LOV(',
'                      p_idx        => 1,',
'                      p_value      => l_value,',
'                      p_lov        => rec.lov,',
'                      p_show_null  => ''NO'',',
'                      p_item_id    => ''P2_''||rec.name) || ''<br>'';',
'            else',
'                l_html := l_html || rec.name || APEX_ITEM.TEXT(',
'                      p_idx        => 1,',
'                      p_value      => l_value,',
'                      p_size       => 30,',
'                      p_maxlength  => 100,',
'                      p_item_id    => ''P2_''||rec.name) || ''<br>'';',
'            end if;',
'      ',
'      END LOOP;',
'    else',
'        l_html := APEX_ITEM.TEXT(',
'                        p_idx         => 11,',
'                        p_value       => ''This extract does not have parameters.'',',
'                        p_size        => 50,',
'                        p_maxlength   => 100);',
'',
'    end if;',
'  return l_html;',
'END;'))
