wwv_flow_imp_page.create_page_process(
 p_id=>wwv_flow_imp.id(30661836136259245)
,p_process_sequence=>10
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'SubmitExtract'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'l_result varchar2(30);',
'l_error_msg varchar2(32767);',
'l_msg_id raw(16);',
'',
'l_json          JSON_OBJECT_T;',
'l_parameters    JSON_ARRAY_T := JSON_ARRAY_T();',
'l_parameter     JSON_OBJECT_T;',
'',
'begin',
'',
'if :P2_EXTRACT_ID is not null then ',
'  ',
'    BEGIN',
'',
'      FOR rec IN (SELECT rownum rn, jt.position, jt.name',
'                FROM DWH_EXTRACT_SETUP des,',
'                     JSON_TABLE(des.bi_parameters, ''$.parameters[*]''',
'                                COLUMNS (',
'                                  position   VARCHAR2(100) PATH ''$.position'',',
'                                  name       VARCHAR2(100) PATH ''$.name'',',
'                                  value       VARCHAR2(100) PATH ''$.value''',
'                                )) jt',
'                where des.id = :P2_EXTRACT_ID',
'                order by jt.position)',
'      LOOP',
'        l_parameter := JSON_OBJECT_T();',
'        l_parameter.put(''position'', rec.position);',
'        l_parameter.put(''name'', rec.name);',
'        ',
'        IF APEX_APPLICATION.G_F01(rec.rn) IS NULL THEN',
'          l_parameter.put_null(''value'');',
'        ELSE',
'          l_parameter.put(''value'', APEX_APPLICATION.G_F01(rec.rn));',
'        END IF;',
'        ',
'        l_parameters.append(l_parameter);',
'      END LOOP;',
'',
'      -- Construct the main JSON object',
'      l_json := JSON_OBJECT_T();',
'      l_json.put(''extract_id'', :P2_EXTRACT_ID);',
'      l_json.put(''parameters'', l_parameters);',
'',
'      ',
'    EXCEPTION',
'      WHEN OTHERS THEN',
'        DBMS_OUTPUT.PUT_LINE(''Error formatting outbound message json: '' || SQLERRM);',
'    END;',
'',
'',
'    IF l_json is not null THEN',
'        dwh_extract_pkg.start_adhoc_extract(',
'            p_extract_id => :P2_EXTRACT_ID,',
'            p_param_json => l_json.to_clob,',
'            p_msg_id     => l_msg_id);',
'',
'        :P2_MSG_ID := l_msg_id;',
'',
'        if l_result != ''Success'' then',
'',
'             apex_error.add_error (',
'                  p_message => l_result||'': ''||l_error_msg,',
'                  p_display_location => apex_error.c_inline_in_notification);',
'        end if;',
'    END IF;',
'',
'else',
'    apex_error.add_error (',
'          p_message => ''Choose which extract to run.'',',
'          p_display_location => apex_error.c_inline_in_notification);',
'',
'end if;',
'',
'end;',
''))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_success_message=>'Extract succesfully started. Message ID &P2_MSG_ID.'
,p_internal_uid=>30661836136259245
);
